sys_script.script:
(function executeRule(current, previous /*null when async*/) {	
	// Get all the approved Cert Acquisition of the lastest submission
	var grSubmission = new GlideRecord('x_424363_ski_eva_1_submission');
	grSubmission.addQuery('assigned_to', current.assigned_to.sys_id);
	grSubmission.addQuery('state', 3);
	grSubmission.addQuery('valid', true);
	grSubmission.query();

	if(grSubmission.next()) {		
		var grCertAcq = new GlideRecord('x_424363_ski_eva_1_cert_acquisition');
		grCertAcq.addQuery('submission', grSubmission.sys_id);
		grCertAcq.query();

		while(grCertAcq.next()) {			
			// Clone the Cert Acquisitions and link to new submission
			var new_acquisition = new GlideRecord('x_424363_ski_eva_1_cert_acquisition');
			new_acquisition.initialize();
			new_acquisition.submission = current.sys_id;
			for (var field in grCertAcq) {
				if (field != 'submission') {
					new_acquisition.setValue(field, grCertAcq.getValue(field));
				}
			}			
			new_acquisition.insert();
		}
	}

})(current, previous);
