sys_script.script:
/*
 * Description:
 *  Create new data for 'skill_assessment' from 'skill' 
 *  Load data from 'skill_assessment' tb to related list on 'submission'
 *  Update skills which are approved earlier
 */

(function executeRule(current, previous /*null when async*/) {
	var submission_id = '';
	// Get the lastest user's Completed submission
	var submission = new GlideRecord('x_424363_ski_eva_1_submission');
	submission.addQuery('assigned_to', current.assigned_to.sys_id);
	submission.addQuery('state', 3);
	submission.addQuery('valid', true);	
	submission.query();
	if(submission.next()){
		submission_id = submission.sys_id;
	}

	//Get all skills
	var grSkill = new GlideRecord('x_424363_ski_eva_1_skill');
	grSkill.query();

	while(grSkill.next()) {
		//Create records for 'skill_assessment' from 'skill'
		var grSkillAssessment = new GlideRecord('x_424363_ski_eva_1_skill_assessment');
		grSkillAssessment.initialize();
		grSkillAssessment.skill = grSkill.sys_id;
		grSkillAssessment.submission = current.sys_id;

		// Update skills info with approved status from previous submission if available
		if(submission_id) {
			var grSkAsmt =  new GlideRecord('x_424363_ski_eva_1_skill_assessment');
			grSkAsmt.addQuery('submission', submission_id);
			grSkAsmt.addQuery('skill', grSkill.sys_id);
			grSkAsmt.addQuery('status', 3);
			grSkAsmt.query();

			if(grSkAsmt.next()) {
				for (var field in grSkAsmt) {
					if(field != 'skill' && field != 'submission') {
						grSkillAssessment.setValue(field, grSkAsmt.getValue(field));
					}
				}
			}
		}

		grSkillAssessment.insert();
	} 
})(current, previous);
