sys_script.script:
/** 
** Abort update if there is no cert acquisition with status new/modified & there is no 
** skill assessment with status (new & proficency != N/A) / modified
*/

(function executeRule(current, previous /*null when async*/) {	
	// Check Skill Assessment list
	var sa_valid_check = false;
	var grSkAsmt = new GlideRecord('x_424363_ski_eva_1_skill_assessment');
grSkAsmt.addEncodedQuery('status=1^proficiency_level!=0^submission=' + current.sys_id + '^NQstatus=2^submission=' + current.sys_id);
	grSkAsmt.query();	
	if(grSkAsmt.next()) {
		sa_valid_check = true;
	}

	// Check Cert Acquisition
	var ca_valid_check = false;
	var grCertAcq = new GlideRecord('x_424363_ski_eva_1_cert_acquisition');
	grCertAcq.addQuery('submission', current.sys_id);
	grCertAcq.addEncodedQuery('statusIN2,1');
	grCertAcq.query();
	if(grCertAcq.next()) {
		ca_valid_check = true;
	}		

	if(!sa_valid_check && !ca_valid_check){ // Submission is empty		
		current.setAbortAction(true);
		current.state = "0";
		action.setRedirectURL(current);
		gs.addErrorMessage('You cannot submit for review when there is no update in Skill Assessment / Cert Acquisition');
	}

})(current, previous);
