sys_script_email.script:
(function runMailScript( /* GlideRecord */ current, /* TemplatePrinter */ template,
    /* Optional EmailOutbound */
    email, /* Optional GlideRecord */ email_action,
    /* Optional GlideRecord */
    event) {
    if (current.state == '1')
        email.setSubject('Submission ' + current.number + ' has been rejected by PM');
    else
        email.setSubject('Submission ' + current.number + ' has been rejected by COE');

	template.print('<div>Hi,</div><br/>');
	template.print('<div>Your submission has been rejected.</div><br/>');
	template.print('<div>Click here to view Submission: ${URI_REF}</div><br/>');
})(current, template, email, email_action, event);
