sys_script_email.script:
(function runMailScript(/* GlideRecord */ current, /* TemplatePrinter */ template,
          /* Optional EmailOutbound */ email, /* Optional GlideRecord */ email_action,
          /* Optional GlideRecord */ event) {

          // Add your code here

	var submission = new GlideRecord('x_424363_ski_eva_1_submission');
	submission.addQuery('valid', true);
    submission.addQuery('state', 3);
    submission.addQuery('assigned_to', current.sys_id);
    submission.setLimit(1);
    submission.query();
	if (submission.next()){
template.print('<a href="https://' + gs.getProperty('instance_name') + '.service-now.com/nav_to.do?uri=x_424363_ski_eva_1_submission.do?sys_id=' + submission.sys_id + '">your last skill evaluation updated</a>');
	}
})(current, template, email, email_action, event);
