var FilterUtil = Class.create();
FilterUtil.prototype = {
    initialize: function() {},
    stateForManagerOrCOE: function() {
        var gr = new GlideRecord('sysapproval_approver');
        gr.addEncodedQuery('state=requested^source_table=x_424363_ski_eva_1_submission^approver='+gs.getUserID());
        gr.query();
        var arr = [];
        while (gr.next()) {
            arr.push(gr.document_id.number);
        }
        return arr.toString();
    },
    type: 'FilterUtil'
};
