sys_ui_action.script:
function approve(){
	// check something
	if(g_scratchpad.rejectedSkillsCount > 0) {
		//Display the selected section
		g_tabs2Sections.setActive(1);

		//Remove any existing field message, set comments mandatory, and show a new field message
		g_form.hideFieldMsg('comments');
		g_form.setMandatory('comments', true);
		g_form.showFieldMsg('comments', 'Comments are mandatory when there\'s any skill rejected in an Submission.', 'error');		
	}
	gsftSubmit(null, g_form.getFormElement(), "btn_approve");
}

if (typeof window == 'undefined'){		
	current.update();	
	if (!current.isActionAborted()) {		
		var currentId = current.getValue('sys_id');
		var userId = gs.getUserID();
		// check if submission is approved for bussiness rule 'Sync comment to submission'
		var session = gs.getSession();
		session.putClientData('isApprovalSubmission', 'true');
		//Query to table sysapproval_approver find record have Approving data is this submission and approver is login user then we will change state to approve
		var approval = new global.Approval();
		approval.ActionApproval('approved', currentId, userId);
		//firing approved event for sending notification
		gs.eventQueue("x_424363_ski_eva_1.submission.approved", current, current.sys_updated_by, gs.getUserDisplayName());
		// After updating the current page , redirect to next record if any
		// Otherwise, stay at current
		var nextSubmission = (new global.Approval()).nextSubmisionRecord_SE(current);
		if(nextSubmission == null)
			action.setRedirectURL(current);
		else
			action.setRedirectURL(nextSubmission);
	}
}
